import timefrom turtle import Screenfrom snake import Snakefrom food import Foodfrom scorebord import Scorescreen = Screen()screen.setup(width=600, height=600)screen.bgcolor('black')screen.title("snake")screen.tracer(0)my_snake = Snake()food = Food()score = Score()screen.listen()screen.onkey(my_snake.up, 'w')screen.onkey(my_snake.down, 's')screen.onkey(my_snake.left, 'a')screen.onkey(my_snake.right, 'd')score_now = 0is_game_on = Truewhile is_game_on:    time.sleep(0.1)    my_snake.move()    screen.update()        # Detect collision with food    if my_snake.head.distance(food) < 15:        food.refresh()        my_snake.extend()        score.increase_score()            # detect collision with walls    if my_snake.head.xcor() > 280 or my_snake.head.xcor() < -280 or\            my_snake.head.ycor() > 280 or my_snake.head.ycor() < -280:        score.game_over()        is_game_on = False            # detect colission with tale    for segment in my_snake.body[1:]:        if my_snake.head.distance(segment) < 10:            is_game_on = False            score.game_over()                        screen.exitonclick()